import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';

function OrderPanel() {
    const navigate = useNavigate();
    const [status, setStatus] = useState();
    const [track,setTrack]=useState({
        trackingid:""
    });
    const [orderdetails,setOrderDetails]=useState({
        productname:"",
        orderid:"",
        address:"",
        tracking:"",
        trackingid:""
    });
    function loaddata(){
        axios.get('http://localhost:8080/login').then((response)=>{
            // console.log(response);
            if(response.data.loggedin===true){
                setStatus(response.data.user[0].username);
            }
            else{
                navigate('/');
            }
        })
        axios.get('http://localhost:8080/showallorders').then((response)=>{
            // console.log(response.data);
            setOrderDetails(response.data);
        })
    }
    useEffect(()=>{

        loaddata();
    },[])


function handleAccept(orderid){
    axios.get(`http://localhost:8080/accept/${orderid}`);
    var ans=prompt("Enter Tracking Id","");
    setTrack(ans);
    console.log(ans);
    axios.post(`http://localhost:8080/settrackingid/${orderid}`,ans);
loaddata();
}


