    <div>
        <h1>Admin Panel Order List</h1>
        <Link to='/home'>
            <button>Home</button>
        </Link>

<div className='listt'>
                <h1>Your Orders</h1>
                <table className='tablee'>

                    <tbody>
                    <tr>
                        <th>Item Id</th>
                        <th>Product Id</th>
                        <th>Username</th>
                        <th>Product Name</th>
                        <th>Order Id</th>
                        <th>Address</th>
                        <th>Tracking Status</th>
                        <th>Tracking Id</th>

                    </tr>
                    {orderdetails && Object.keys(orderdetails).map(key => {
                        const item = orderdetails[key];
                        return (
                            <tr key={item.id} className=' '>
                                <td>{item.id}</td>
                                <td>{item.pid}</td>
                                <td>{item.username}</td>
                                <td>{item.productname}</td>
                                <td>{item.orderid}</td>
                                <td>{item.address}</td>
                                {/* <td>{item.tracking}</td> */}
                                {item.tracking === 0 && (
                                        <>
                                        <td>
                                            <button onClick={() => handleAccept(item.orderid)} >Accept</button>
                                            <button onClick={() => handleDecline(item.orderid)} >Decline</button>
                                        </td>

                                        </>
                                    )}
                                    <td>{item.tracking === 1 ? 'Confirmed' : ""}</td>
                                <td>{item.trackingid}</td>


                            </tr>

                        )
                    })}
                    </tbody>
                </table>
            </div>

















    </div>
  )
}

export default OrderPanel
