import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import './Admin.css'
function Admin() {
    const navigate = useNavigate();
    const [status, setStatus] = useState();


    const [product, setProduct] = useState({
        productname: "",
        category: "",
        image_path: "0",
        description: "",
        price: "",
        saleprice: ""
    })

    useEffect(() => {
        axios.get('http://localhost:8080/login').then((response) => {
            console.log(response);
            if (response.data.loggedin === true) {
                setStatus(response.data.user[0].username);
            }
            else {
                navigate('/');
            }
        })
    }, [])

    useEffect(() => {
        async function loaddata() {
            const response = await axios.get('http://localhost:8080/showcategory', user);
            setData(response.data);
            const response1 = await axios.get('http://localhost:8080/showproduct', product);

            setProduct(response1.data);
        }
        loaddata();


    })



    function logout() {
        axios.get('http://localhost:8080/logout').then((response) => {
            console.log(response);
            navigate('/');
        })
    }
    const [data, setData] = useState([]);
    const [user, setUser] = useState({
        categoryname: "",
    })
    function handlechange(event) {
        let name = event.target.name;
        let value = event.target.value;
        setUser((preval) => {
            return {
                ...preval,
                [name]: [value]
            }
        })
    }
    function handlesubmit(event) {
        if (!user.categoryname) {
            alert("Enter details");
        }
        else {
            axios.post('http://localhost:8080/addcategory', user).then((response) => {
                setUser({ categoryname: "" })
                alert("Category Added");
            })
        }
    }



    function handlechange1(event) {
        let name = event.target.name;
        let value = event.target.value;
        setProduct((preval) => {
            return {
                ...preval,
                [name]: [value]
            }
        })
    }


    function handlesubmit1(event) {
        if (!product.productname || !product.category || !product.description || !product.price || !product.saleprice || !product.image_path) {
            alert("Enter Details");
        }
        else {
            axios.post('http://localhost:8080/addproduct', product).then((response) => {
                setProduct({
                    productname: "",
                    category: "",
                    image_path: "0",
                    description: "",
                    price: 0,
                    saleprice: 0
                })
                alert("Product Added");
            })
        }
    }



    return (
        <div>
            <div className='navbar'>
                <h1>Admin Panel</h1>
                <h2>{status}</h2>
                <button onClick={logout}>Logout</button>
            </div>

            <div>

            </div>




            <div className='list'>
                <h1>List of Categories</h1>
                {data.map((item) => {

                    return (
                        <div className='flexx '>
                            <p>{item.id}</p>
                            <p>{item.categoryname}</p>

                        </div>

                    )
                })}
            </div>



            <div className='list'>
                <h1>List of Products</h1>
                {Object.keys(product).map(key => {
                    const item = product[key];
                    return (
                        <div className='flexx '>
                            <p>{item.id}</p>
                            <p>{item.productname}</p>
                            <p>{item.category}</p>
                            <p>{item.description}</p>
                            <p>{item.price}</p>
                            <p>{item.saleprice}</p>

                        </div>

                    )
                })}
            </div>


        </div>
    )
}

export default Admin
