import React, { useState } from 'react'

import axios from 'axios'


axios.defaults.withCredentials=true;
function Login() {
    const [loginStatus,setLoginStatus]=useState();
    const [user, setUser] = useState({
        email: "",
        password: ""
    })

    function hashing(password) {
        password = password.toString();
        const search = 'a';
        const replaceWith = '-';

        const ans = password.split(search).join(replaceWith);
        return ans;
    }
    function handlechange(event) {
        let value = event.target.value;
        let name = event.target.name;
        setUser((preval) => {
            return {
                ...preval,
                [name]: [value]
            }
        })

    }
    const navigate=useNavigate();

    function handlesubmit(event) {
        console.log(user);
        if (!user.email || !user.password) {
            alert("Please provide data");
        }
        else {
            user.password = hashing(user.password);
            axios.post("http://localhost:8080/login",user).then((response)=>{
                setUser({email:"",password:""})
                if(response.data.message){
                    setLoginStatus(response.data.message);
                }
                else{
                    setLoginStatus(response.data[0].username);
                    if(response.data[0].type==1){
                        setTimeout(()=>navigate('/admin'),500)
                    }
                    else{
                        setTimeout(()=>navigate('/home'),500)
                    }
                }
            }).catch((err)=>{
                console.log(err);
            })

        }
    }


    return (
        <div>
            <h3>Login Here</h3>
            <p>Email Id</p>
            <input type='text' name='email' value={user.email} onChange={handlechange} /><br />

            <p>Password</p>
            <input type='text' name='password' value={user.password} onChange={handlechange} /><br />

            <button onClick={handlesubmit}>Login</button>
        </div>
    )
}

export default Login
