import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import './Admin.css'
function Admin() {
    const navigate=useNavigate();
    const [status,setStatus]=useState();
    const [product,setProduct]=useState({
    productname:"",
    category:"",
    image_path:"0",
    description:"",
    price:"",
    saleprice:""
})

    useEffect(()=>{
        axios.get('http://localhost:8080/login').then((response)=>{
            console.log(response);
            if(response.data.loggedin===true){
                setStatus(response.data.user[0].username);
            }
            else{
                navigate('/');
            }
        })
    },[])

    useEffect(()=>{
        async function loaddata(){
            const response= await axios.get('http://localhost:8080/showcategory',user);
             setData(response.data);
            const response1= await  axios.get('http://localhost:8080/showproduct',product);
            const product = Object.values(response1.data);
            setProduct(product);
        }
        loaddata();
        console.log(typeof product)

    })

