//             setProduct(response.data);
//         })

    // useEffect(()=>{
    //     axios.get('http://localhost:8080/showproduct',product).then((response)=>{
    //         setProduct(response.data);
    //     })
    // })









    function logout(){
        axios.get('http://localhost:8080/logout').then((response)=>{
            console.log(response);
            navigate('/');
    })
}
const[data,setData]=useState([]);
const [user,setUser]=useState({
    categoryname:"",
})
function handlechange(event){
    let name=event.target.name;
    let value=event.target.value;
    setUser((preval)=>{
        return{
            ...preval,
            [name]:[value]
        }
    })
}
function handlesubmit(event){
    if(!user.categoryname){
        alert("Enter details");
    }
    else{
        axios.post('http://localhost:8080/addcategory',user).then((response)=>{
            setUser({categoryname:""})
            alert("Category Added");
        })
    }
}

// const [product,setProduct]=useState({
