{ product&& product.map((item) => {

            return (
              <div className='flexx '>
                <p>{item.id}</p>
                <p>{item.productname}</p>
                <p>{item.category}</p>
                <p>{item.description}</p>
                <p>{item.price}</p>
                <p>{item.saleprice}</p>

              </div>

            )
          })}
</div>


    </div>
  )
