import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import './Admin.css'
function Admin() {
    const navigate=useNavigate();
    const [status,setStatus]=useState();
    useEffect(()=>{
        axios.get('http://localhost:8080/login').then((response)=>{
            console.log(response);
            if(response.data.loggedin===true){
                setStatus(response.data.user[0].username);
            }
            else{
                navigate('/');
            }
        })
    },[])

    useEffect(()=>{
        axios.get('http://localhost:8080/showcategory',user).then((response)=>{
            setData(response.data);
        })
    })


    function logout(){
        axios.get('http://localhost:8080/logout').then((response)=>{
            console.log(response);
            navigate('/');
    })
}
const[data,setData]=useState([]);
const [user,setUser]=useState({
    categoryname:"",
})
function handlechange(event){
    let name=event.target.name;
    let value=event.target.value;
    setUser((preval)=>{
        return{
            ...preval,
            [name]:[value]
        }
    })
}
function handlesubmit(event){
    if(!user.categoryname){
        alert("Enter details");
    }
    else{
        axios.post('http://localhost:8080/addcategory',user).then((response)=>{
            setUser({categoryname:""})
            alert("Category Added");
        })
    }
}

  return (
    <div>
        <h1>Home</h1>
        <h2>{status}</h2>
        <button onClick={logout}>Logout</button>

<div>
