        if(user.type=='1'){
            user.type=check();
        }
        var date=new Date();
            date=date.toISOString().slice(0, 19).replace('T', ' ');
            user.timecreated=date;
        user.password=hashing(user.password);
        axios.post("http://localhost:8080/adduser",user).then((response)=>{
            setUser({
                email:'',
                password:'',
                username:'',
                timecreated:"",
                type:0
            })
        }).catch((err)=>{
            console.log(err);
        })
        setTimeout(()=>navigate('/'),500)
    }
}



