import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import './Admin.css'
function Admin() {
    const navigate = useNavigate();
    const [status, setStatus] = useState();


    const [product, setProduct] = useState({
        productname: "",
        category: "",
        image_path: "0",
        description: "",
        price: "",
        saleprice: ""
    })

    useEffect(() => {
        axios.get('http://localhost:8080/login').then((response) => {
            console.log(response);
            if (response.data.loggedin === true) {
                setStatus(response.data.user[0].username);
            }
            else {
                navigate('/');
            }
        })
    }, [])

    useEffect(() => {
        async function loaddata() {
            const response = await axios.get('http://localhost:8080/showcategory', user);
            setData(response.data);
            const response1 = await axios.get('http://localhost:8080/showproduct', product);

            setProduct(response1.data);
        }
        loaddata();


    })



    function logout() {
        axios.get('http://localhost:8080/logout').then((response) => {
            console.log(response);
            navigate('/');
        })
    }
    const [data, setData] = useState([]);
    const [user, setUser] = useState({
        categoryname: "",
    })
    function handlechange(event) {
        let name = event.target.name;
        let value = event.target.value;
        setUser((preval) => {
            return {
                ...preval,
                [name]: [value]
            }
        })
    }
    function handlesubmit(event) {
        if (!user.categoryname) {
            alert("Enter details");
        }
        else {
            axios.post('http://localhost:8080/addcategory', user).then((response) => {
                setUser({ categoryname: "" })
                alert("Category Added");
            })
        }
    }



    function handlechange1(event) {
        let name = event.target.name;
        let value = event.target.value;
        setProduct((preval) => {
            return {
                ...preval,
                [name]: [value]
            }
        })
    }

    function handlechange11(event){
        product.category=event.target.value;
    }

    function handlesubmit1(event) {
        if (!product.productname || !product.category || !product.description || !product.price || !product.saleprice || !product.image_path) {
            alert("Enter Details");
        }
        else {
            axios.post('http://localhost:8080/addproduct', product).then((response) => {
                setProduct({
                    productname: "",
                    category: "",
                    image_path: "0",
                    description: "",
                    price: 0,
                    saleprice: 0
                })
                alert("Product Added");
            })
        }
    }



    return (
        <div>
            <div className='navbar'>
                <div>
                    <h1>Admin Panel</h1>
                    <h2>Welcome {status}</h2>
                </div>
                <button onClick={logout}>Logout</button>
            </div>

            <div className='topbox'>
                <div className='box'>
                    <h1 className='boxhead'>Add Category</h1>
                    <p>Enter Category Name</p>
                    <input type='text' onChange={handlechange} name='categoryname' value={user.categoryname} />
                    <button onClick={handlesubmit}>Submit</button>
                    <div className='list'>
                        <h1>List of Categories</h1>
                        <table className='table'>
                            <tr>
                                <th>Id.No</th>
                                <th>Category Name</th>
                            </tr>
                            {data.map((item) => {
                                return (
                                    <tr className='flexx '>
                                        <td>{item.id}</td>
                                        <td>{item.categoryname}</td>
                                        <td><button >Delete</button></td>
                                    </tr>
                                )
                            })}
                        </table>
                    </div>
                </div>


                <div className='box'>
                <h1 className='boxhead'>Add Product</h1>
                    <div className='boxinput'>


                        <p>ProductName</p>
                        <input type='text' onChange={handlechange1} name='productname' value={product.productname} />
                        <p>Category</p>
                        {/* <input type='text' onChange={handlechange1} name='category' value={product.category} /> */}
                        <select name='category' onChange={handlechange11} value={product.category} >
                        {data.map((item) => {
                                return (

                                        <option value={item.categoryname}>{item.categoryname}</option>

                                )
                            })}
                        </select>



                        <p>ImagePath</p>
                        <input type='text' onChange={handlechange1} name='image_path' value={product.image_path} />
                        <p>Description</p>
                        <input type='text' onChange={handlechange1} name='description' value={product.description} />
                        <p>Price</p>
                        <input type='text' onChange={handlechange1} name='price' value={product.price} />
                        <p>Sale Price</p>
                        <input type='text' onChange={handlechange1} name='saleprice' value={product.saleprice} />
                            <p> </p>
                        <button onClick={handlesubmit1}>Add Product</button>
                    </div>

                </div>
            </div>








            <div className='listt'>
            <h1>List of Products</h1>
                <table className='tablee'>

                    <tr>
                        <th>Item Id</th>
                        <th>Product Name</th>
                        <th>Category</th>
                        <th>Description</th>
                        <th>Price</th>
                        <th>Sale Price</th>
                    </tr>
                    {Object.keys(product).map(key => {
                    const item = product[key];
                    return (
                        <tr className=' '>
                            <td>{item.id}</td>
                            <td>{item.productname}</td>
                            <td>{item.category}</td>
                            <td>{item.description}</td>
                            <td>{item.price}</td>
                            <td>{item.saleprice}</td>

                        </tr>

                    )
                })}
                </table>
            </div>


        </div>
    )
}

export default Admin
